mapper.select:
select o.id, o.is_pay, o.order_no, o.create_time, o.status, comm.id commid,comm.name, comm.price, comm.picture, comm.shipping_cost, seller.id sellerid, seller.nickname sellername, receiver.receiver, receiver.address, receiver.phone, buyer.id buyerid, buyer.nickname buyername, buyer.phone buyerphone from shop_order o left JOIN shop_commodity comm on comm.id = o.cid left JOIN shop_user seller on seller.id = comm.uid left JOIN shop_receiving receiver on receiver.id = o.rid left JOIN shop_user buyer on buyer.id = o.uid where o.uid = #{userId}
and is_pay = #{payStatus}
limit #{offset}, #{limit};

select o.id, o.create_time, o.status, comm.id commid, comm.name, comm.picture, comm.price, seller.id sellerid, seller.nickname sellername, buyer.id buyerid, buyer.nickname, r.id recid, r.receiver, r.address, r.phone from ((((shop_order o left JOIN shop_commodity comm on o.cid = comm.id) left JOIN shop_user seller on comm.uid = seller.id) left JOIN shop_user buyer on o.uid = buyer.id) left JOIN shop_receiving r on r.id = o.rid) where comm.uid = #{sellerid}
and status = #{deliverystatuts}
limit #{offset}, #{limit}
