mapper.select:
SELECT id,avatar,nickname,birthday,email,phone
FROM shop_user
WHERE account = #{account} AND password = #{password}

mapper.update:
UPDATE shop_user
SET nickname = #{nickname}, avatar = #{avatar}, sex = #{sex}, birthday = #{birthday}, email = #{email}
WHERE id = #{id}

UPDATE shop_user
SET password = #{password}
WHERE email = #{email}
