mapper.select:
SELECT id,account,avatar,nickname,birthday,email,phone,acive
FROM shop_user
WHERE account = #{account} AND password = #{password}

mapper.update:
UPDATE shop_user
SET nickname = #{nickname}, avatar = #{avatar}, sex = #{sex}, birthday = #{birthday}, email = #{email}
WHERE id = #{id}

UPDATE shop_user
SET password = #{password}
WHERE email = #{email}

UPDATE shop_user
SET active = 1
WHERE email = #{email}
